mapper.insert:
insert into sys_user_address (
    user_id, receiver_name, receiver_phone, address, is_default, create_time
) values (
             #{userId}, #{receiverName}, #{receiverPhone}, #{address}, #{isDefault}, sysdate()
         )

mapper.update:
update sys_user_address
receiver_name = #{receiverName}, receiver_phone = #{receiverPhone}, address = #{address}, is_default = #{isDefault}, update_time = sysdate()
where address_id = #{addressId}

update sys_user_address
set is_default = 1, update_time = sysdate()
where address_id = #{addressId} and user_id = #{userId}
